import React, { useState } from 'react'
import Table from 'react-bootstrap/Table'
import 'bootstrap/dist/css/bootstrap.min.css'
//import datamessage from './datamessage.json'

const socket = new WebSocket('ws://localhost:5000/', "protocolOne");

function Message(){

    const [ dataDB, setDataDB ] = useState('');

    socket.onmessage = function(event) {
        //var message = event.data;
        console.log('есть сообщение')
        //const dateline = JSON.parse(message);
        //console.log(dateline);
        showMessage(event.data);
      }
      
      //console.log(dateline)
    
      function showMessage(message) {
        var messageSignal = document.createElement('div');
        messageSignal.appendChild(document.createTextNode(message));
        setDataDB(JSON.parse(message));
      }
      console.log(dataDB);
      var datamessage = dataDB.data;
      console.log(datamessage);
    //const datamessage = localStorage.getItem("data") ? JSON.parse(localStorage.getItem("data")) : [];

    const messageJson = datamessage.map(
        (info) => {
            //допилить
            /*if (info.priority == "very tall"){
                console.log('ошибка')
            }*/

            return(
                <tr key={info.id}>
                    <th>{info.id}</th>
                    <th>{info.time}</th>
                    <th>{info.priority}</th>
                    <th>{info.gruppa}</th>
                    <th>{info.status}</th>
                    <th>{info.message}</th>
                </tr>
            )
        }
    )

    return (
        <div>
            <div>
                <h1>Таблица объектов</h1> 
            </div>
            <div className="table mt-5">
                <Table striped bordered hover size='sm'>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>время</th>
                            <th>приоритет</th>
                            <th>группа</th>
                            <th>статус</th>
                            <th>Сообщение</th>
                        </tr>
                    </thead>
                    <tbody>
                            {messageJson}
                    </tbody>
                </Table>
            </div>
        </div>
    )
}

export default Message;
